import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
    is_register:boolean=false;
    username:string='';
    password:string='';
    email:string='';
    firstname:string='';
    lastname:string='';


    constructor(private http:HttpClient) {}

  toggleRegistering()
  {
    this.is_register=!this.is_register;
  }

  onSubmit()
  {
    if(this.is_register)
    {
      const registration_data={
        firstname:this.firstname,
        lastname:this.lastname,
        username:this.username,
        email:this.email,
        password:this.password

      }

    this.http.post('api/register',registration_data).subscribe(
    (response:any)=>{
      if(response.success){
      console.log('Registration succesful');
      }
    else
    {
      console.log('registration failed',response.message);
    }
    }
    );

    }else
    {
      const login_data={
        username:this.username,
        password:this.password
      };

      this.http.post('api/login',login_data).subscribe(
        (response:any)=>{
        if(response.success)
        {
          console.log('Registration succesful');
        }
        else
        {
          console.log('registration failed',response.message);
        }
      }
      );

    }







  }


}
<div style="background-color: #e6e6fa; height:fit-content">
    <h1>{{ is_register ? 'Register' : 'Login' }}</h1>
    <form (ngSubmit)="onSubmit()">
        <div>
        <label for="username">Username</label>
        <input type="text" id="username" [(ngModel)]="username" name="username" required>
        </div>
        <div>
        <label for="password">Password</label>
        <input type="password" id="password" [(ngModel)]="password" name="password" required>
        </div>

        <ng-container *ngIf="is_register">
            <div>
            <label for="firstName">First Name</label>
            <input type="text" id="firstName" [(ngModel)]="firstname" name="firstname">
            </div>
            <div>
            <label for="lastName">Last Name</label>
            <input type="text" id="lastName" [(ngModel)]="lastname" name="lastname">
            </div>
            <div>
            <label for="Email">Email</label>
            <input type="text" id="Email" [(ngModel)]="email" name="email">
            </div>
        </ng-container>



        <button type="submit">{{ is_register ? 'Register' : 'Login' }}</button>
    </form>


    <button *ngIf="!is_register" (click)="toggleRegistering()">Create Account</button>
</div>
