<style>
.center {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 20px;

  }

  .btn-bd-primary {
  --bs-btn-font-weight: 600;
  --bs-btn-color: var(--bs-white);
  --bs-btn-bg: var(--bd-violet-bg);
  --bs-btn-border-color: var(--bd-violet-bg);
  --bs-btn-hover-color: var(--bs-white);
  --bs-btn-hover-bg: #{shade-color($bd-violet, 10%)};
  --bs-btn-hover-border-color: #{shade-color($bd-violet, 10%)};
  --bs-btn-focus-shadow-rgb: var(--bd-violet-rgb);
  --bs-btn-active-color: var(--bs-btn-hover-color);
  --bs-btn-active-bg: #{shade-color($bd-violet, 20%)};
  --bs-btn-active-border-color: #{shade-color($bd-violet, 20%)};
}


</style>





<div class="d-flex flex-row mb-3">

<div>
<h3> Car Form</h3>
<form [formGroup]="checkoutForm" (ngSubmit)="createCar()" class="card text-bg-success m-2" style="width: 18rem;">



    <div class="form-group">
      <label for="brand">Brand</label>
      <input type="text"  id="brand"
             formControlName="brand" >
    </div>

    <div class="form-group">
      <label for="model">Model</label>
      <input type="text" id="model"
      formControlName="model">

    </div>


    <div class="form-group">
        <label for="productionYear">Production Year</label>
        <input type="text" id="productionYear"
              formControlName="productionYear">

      </div>

      <div class="form-group">
        <label for="stock">Stock</label>
        <input type="text" id="stock"
              formControlName="stock">

      </div>  


      <button type="submit" class="btn btn-dark me-2 " style="width: 7rem; margin-left: 5rem" >Create Car</button>





</form>
</div>

<!--<button (click)="createCar()" type="button" class="btn btn-primary me-2">Create Car</button>-->
<div>
  <table>
<div *ngFor="let car of cars" class="card text-bg-success m-2 carrow" margin-top="21px" style="width: 40rem;" > <td style="width:350px ;">{{car.idCar}} - {{car.brand}} - {{car.model}} - {{car.productionYear}} - {{car.stock}}</td>
<tr>
  <div class="center" >

<td style=" margin-top:25px; ">  <button (click)="showCar(car)" type="button" class="btn btn-dark me-2 " style="width: 7rem;" >Show Car</button> </td>

 <td style=" margin-top:25px"> <button (click)="deleteCar(car)" type="button" class="btn btn-dark me-2" >Delete</button></td>

 <td style=" margin-top:25px"> <button (click)="updateCar(car)" type="button" class="btn btn-dark me-2" >Edit</button></td>
  </div>
  </tr>
</div>
</table>

</div>

</div>
