    align-items: center;
    height: 20px;

  }
</style>





<button (click)="createCar()" type="button" class="btn btn-primary me-2">Create Car

    <div class="form-group">
        <label for="idCar">Id</label>
        <select class="form-control" id="idCar" required>
          <option *ngFor="let car of cars" [value]="car">{{idCar}}</option>
        </select>
      </div>

</button>


<div *ngFor="let car of cars">{{car.idCar}} - {{car.brand}} - {{car.model}} 

    <div class="center">

    <button (click)="showCar(car)" type="button" class="btn btn-primary me-2">Show Car</button> 

    <button (click)="deleteCar(car)" type="button" class="btn btn-danger me-2">Delete</button>

    <button (click)="deleteCar(car)" type="button" class="btn btn-danger me-2">Update</button>
</div>


</div>
