import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { CarService } from '../../services/car.service';
import { ICar } from '../../models/car';
import { Router } from '@angular/router';
import { FormControl } from '@angular/forms';

@Component({
  selector: 'app-index-cars',
  templateUrl: './index-cars.component.html',
  styleUrls: ['./index-cars.component.css']
})
export class IndexCarsComponent implements OnInit {

  cars!: ICar[];

  constructor(private carService:CarService, private router: Router) {}

  ngOnInit(): void {
    this.carService.getCars().subscribe(res => {
      this.cars =res;
    console.log(res); }
    )
   }

   showCar(car: ICar)
   {
    this.router.navigate([`/cars/${car.idCar}`]);
   }


   createCar() {

    const car: ICar={
    brand: 'VW',
    model: '343',
    productionYear:2022,
    stock: 3
    };

  /*const br: string = new FormControl<string>('').value!;
  car.brand = br;

  const md: string = new FormControl<string>('').value!;
  car.model = md;

  const py: number = new FormControl<number>(0).value!;
  car.productionYear = py;

  const st: number = new FormControl<number>(0).value!;
  car.stock = st;

*/





  // this will be replaced with form value
  this.carService.createCar(car).subscribe(
    res => this.cars.push(res), 
  );
}



  deleteCar(car: ICar)
  {
    this.carService.deleteCar(car.idCar).subscribe(
      (res: any)=> {
        const carIndex = this.cars.indexOf(car);
        this.cars.splice(carIndex,1);
      }
    )
  }

}
