    <button (click)="showCar(car)" type="button" class="btn btn-primary me-2">Show Car</button> 

    <button (click)="deleteCar(car)" type="button" class="btn btn-danger me-2">Delete</button>

    <button (click)="deleteCar(car)" type="button" class="btn btn-danger">Update</button>



</p>
