<div class="d-flex flex-row mb-3">

<div>
<h3> Car Form</h3>
<form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()" class="card text-bg-success m-2" style="width: 12rem;">



    <div class="form-group">
      <label for="brand">Brand</label>
      <p>
      <input type="text"  id="brand"
             formControlName="brand" ></p>
    </div>

    <div class="form-group">
      <label for="model">Model</label>
      <p>
      <input type="text" id="model"
      formControlName="model"></p>

    </div>


    <div class="form-group">
        <label for="productionYear">Production Year</label>
        <p>
        <input type="text" id="productionYear"
              formControlName="productionYear"></p>

      </div>

      <div class="form-group">
        <label for="stock">Stock</label>
        <p>
        <input type="text" id="stock"
              formControlName="stock"></p>

      </div>  


      <button type="submit" class="btn btn-dark me-2 " style="width: 7rem; margin-left: 2rem" >Create Car</button>





</form>
</div>

<!--<button (click)="createCar()" type="button" class="btn btn-primary me-2">Create Car</button>-->
<div>
  <table>
<div *ngFor="let car of cars" class="card text-bg-success m-2 carrow" margin-top="21px" style="width: 40rem;" > <td style="width:350px ;">{{car.idCar}} - {{car.brand}} - {{car.model}} - {{car.productionYear}} - {{car.stock}}</td>
<tr>
  <div class="center" >

<td style=" margin-top:25px; ">  <button (click)="showCar(car)" type="button" class="btn btn-dark me-2 " style="width: 7rem;" >Show Car</button> </td>

 <td style=" margin-top:25px"> <button (click)="deleteCar(car)" type="button" class="btn btn-dark me-2" >Delete</button></td>

 <td style=" margin-top:25px"> <button (click)="updateCar(car)" type="button" class="btn btn-dark me-2" >Edit</button></td>
  </div>
  </tr>
</div>
</table>

</div>

</div>
