import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { CarService } from '../../services/car.service';
import { ICar } from '../../models/car';
import { Router } from '@angular/router';
import { FormBuilder, FormControl,  FormGroup } from '@angular/forms';

@Component({
  selector: 'app-index-cars',
  templateUrl: './index-cars.component.html',
  styleUrls: ['./index-cars.component.css']
})
export class IndexCarsComponent implements OnInit {

  cars!: ICar[];
//adaugare


  brand2:string='';
  model2:string='';
  productionYear2=0;
  stock2=0;
  checkoutForm:FormGroup;

constructor(
    private carService:CarService,
    private router:Router,
    private formBuilder: FormBuilder,
    ){
      this.checkoutForm=this.formBuilder.group({

        brand:'',
        model:'',
        productionYear:0,
        stock:0
      });

    }


  ngOnInit(): void {
    this.carService.getCars().subscribe(res => {
      this.cars =res;
    console.log(res);
    this.checkoutForm.patchValue(res[0]);
   }

    )
   }

   showCar(car: ICar)
   {
    this.router.navigate([`/cars/${car.idCar}`]);
   }

  createCar() {

     let car:ICar={

      brand:this.brand2,
    model:this.model2,
    productionYear: this.productionYear2,
    stock:this.stock2

    };

// this will be replaced with form value
  this.carService.createCar(car).subscribe(
    res => this.cars.push(res), 
  );
}

onSubmit(): void {
      // Process checkout data here

        this.brand2= this.checkoutForm.get('brand')?.value;

        this.stock2= this.checkoutForm.get('stock')?.value;
        this.productionYear2= this.checkoutForm.get('productionYear')?.value;
        this.model2= this.checkoutForm.get('model')?.value;
;      
      this.createCar();
      console.warn('The car has been created', this.checkoutForm.value);
      this.checkoutForm.reset();
    }

  deleteCar(car: ICar)
  {
    this.carService.deleteCar(car.idCar).subscribe(
      (res: any)=> {
        const carIndex = this.cars.indexOf(car);
        this.cars.splice(carIndex,1);
      }
    )
  }


  updateCar(car:ICar)
  {
    const updatedCar: ICar = {
    brand:this.brand2,
    model:this.model2,
    productionYear: this.productionYear2,
    stock:this.stock2
    };
    this.carService.updateCar(car.idCar, updatedCar).subscribe(
      res => {
        this.checkoutForm.patchValue(car);
      }
    );
    }

}
<div style="display:flex; justify-content: center;margin-bottom: 20px;">
 <div>
  <div>
<h3> Add a Car for renting</h3>
  </div>
  <div>
<form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()" class="card" style="width: 20rem">

   <div style="display: flex;justify-content: center; flex-direction: column;">
    <div class="form-group">
      <label for="brand">Brand</label>
      <p>
      <input type="text"  id="brand"
             formControlName="brand" style="width:18rem"></p>
    </div>

    <div class="form-group">
      <label for="model">Model</label>
      <p>
      <input type="text" id="model"
      formControlName="model" style="width:18rem"></p>

    </div>


    <div class="form-group">
        <label for="productionYear">Production Year</label>
        <p>
        <input type="text" id="productionYear"
              formControlName="productionYear" style="width:18rem"></p>

      </div>

      <div class="form-group">
        <label for="stock">Stock</label>
        <p>
        <input type="text" id="stock"
              formControlName="stock" style="width:18rem"></p>

      </div>  

  <div class="buttons">
      <button type="submit" class="btn" style="width: 7rem; display: flex; justify-content: center;" >Create Car</button>
  </div>
   </div>



</form>
  </div>
  </div>
</div>


<div style="display:flex;flex-direction:row;flex-wrap: wrap;justify-content: space-around; ">
<div *ngFor="let car of cars" >
<div *ngIf="car">
  <div class="card " style="width: 18rem;">
      <div class="card-body">
        <h5 class="card-title">{{car.brand}}</h5>
        <div class="card-text">
        <div>
        model: {{car.model}}
        </div>
        <div>
        brand: {{car.brand}}
        </div>
        <div>
        year: {{car.productionYear}}
        </div>

      </div>



      </div>
      <div class="buttons">
      <button (click)="showCar(car)" type="button" class="btn" style="width: 5rem;" >Rent</button> 

      <button (click)="deleteCar(car)" type="button" class="btn" style="width: 5rem;">Delete</button>

     <button (click)="updateCar(car)" type="button" class="btn" style="width: 5rem;">Edit</button>
    </div>

    </div>
  </div>
</div>
</div>
