<p *ngFor="let car of cars$">{{car.idCar}} - {{car.brand}} - {{car.model}}   
    <button (click)="showCar(car)" type="button" class="btn btn-primary me-2">Show Car</button>

    <button (click)="deleteCar(car)" type="button" class="btn btn-danger">Delete</button>
</p>
